package bucles;

public class Bucle_for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//For(expresion:inicial; condición; incremento)
		
		int i=20;
		
		for (i=1; i<=10; i++) {
			System.out.print(i + " ");
		}
		System.out.println(i);
		//Si declaro una variable dentro del bucle sólo sirve para el bucle. Cuando salimos, esa variable es como si no existiese. 
		
		
		
	}

}
